import chromadb

def verificar_dados_chromadb():
    # Conectar ao ChromaDB
    chroma_client = chromadb.PersistentClient(path="./chroma_db")  # Caminho do DB

    # Acessar a coleção "imoveis"
    collection = chroma_client.get_collection(name="imoveis")

    # Verificar se a coleção existe
    if not collection:
        print("Coleção 'imoveis' não encontrada.")
        return {"status": "erro", "message": "Coleção 'imoveis' não encontrada."}

    # Consultar todos os documentos na coleção
    results = collection.get(include=["metadatas", "documents"])

    # Estruturar os resultados para retornar no formato esperado (Data)
    if results["documents"]:
        dados_imoveis = []
        for i, doc in enumerate(results["documents"]):
            metadata = results["metadatas"][i]
            imovel = {
                "descricao": doc,
                "tipo": metadata.get("tipo", "N/A"),
                "localizacao": metadata.get("localizacao", "N/A"),
                "preco": metadata.get("preco", "N/A")
            }
            dados_imoveis.append(imovel)
        return {"status": "sucesso", "dados": dados_imoveis}
    else:
        return {"status": "erro", "message": "Nenhum dado encontrado na coleção 'imoveis'."}

# Chamada da função
result = verificar_dados_chromadb()
print(result)
